function extractNavigation() {
  // Clear any existing states
  document.body.className = document.body.className.replace(/state--category-[a-zA-Z-]+/g, '');

  const navigationTree = [];

  // Main navigation categories and their corresponding state classes
  const categories = [
    { name: 'Woman', stateClass: 'state--category-shop-woman', categoryId: 'category-shop-woman' },
    { name: 'Man', stateClass: 'state--category-shop-man', categoryId: 'category-shop-man' },
    { name: 'Bags', stateClass: 'state--category-bags', categoryId: 'category-bags' },
    { name: 'Scarves', stateClass: 'state--category-scarves', categoryId: 'category-scarves' },
    { name: 'Gifts', stateClass: 'state--category-gifts', categoryId: 'category-gifts' },
    { name: 'Sale', stateClass: 'state--category-sale', categoryId: 'category-sale' }
  ];

  for (const category of categories) {
    // Add the state class to reveal the navigation
    document.body.classList.add(category.stateClass);

    // Find the navigation element
    const navElement = document.getElementById(category.categoryId);

    const categoryData = {
      name: category.name,
      url: null, // Top-level categories don't have direct URLs
      children: []
    };

    if (navElement) {
      // Extract all unique links from the navigation
      const links = navElement.querySelectorAll('a');
      const seenUrls = new Set();

      for (const link of links) {
        const text = link.textContent.trim();
        const url = link.href;

        // Skip duplicate URLs and empty text
        if (!text || seenUrls.has(url)) continue;
        seenUrls.add(url);

        categoryData.children.push({
          name: text,
          url: url,
          children: []
        });
      }
    }

    navigationTree.push(categoryData);

    // Remove the state class
    document.body.classList.remove(category.stateClass);
  }

  return navigationTree;
}
