function extractNavigation() {
  const results = [];
  const seenUrls = new Set();

  // Find all navigation links that contain collections
  const navLinks = document.querySelectorAll('nav a[href*="/collections"]');

  navLinks.forEach(link => {
    const name = link.textContent.trim();
    const url = link.href;

    // Skip duplicates and empty names
    if (name && url && !seenUrls.has(url)) {
      seenUrls.add(url);
      results.push({
        name: name,
        url: url,
        children: []
      });
    }
  });

  return results;
}
