async function extractNavigation() {
  const tree = [];
  const collections = [
    { name: 'AW25', url: 'https://www.entirestudios.com/collection/aw25' },
    { name: 'Uniform', url: 'https://www.entirestudios.com/uniform' },
    { name: 'SS25', url: 'https://www.entirestudios.com/collection/drop-10' },
    { name: 'Archive', url: 'https://www.entirestudios.com/virtual-pop-up' }
  ];

  const categories = ['outerwear', 'tops', 'bottoms', 'dresses', 'knitwear', 'denim', 'leather', 'suiting'];

  for (const col of collections) {
    const node = { name: col.name, url: col.url, children: [] };
    if (col.url.includes('/collection/')) {
      categories.forEach(cat => {
        node.children.push({
          name: cat.charAt(0).toUpperCase() + cat.slice(1),
          url: `${col.url}?tag=${cat}`,
          children: []
        });
      });
    }
    tree.push(node);
  }
  return tree;
}
