async function extractNavigation() {
  const dataPath = '__NEXT_DATA__.props.pageProps.headerData.menuItems';
  const parts = dataPath.split('.');
  let obj = window;
  for (const part of parts) {
    obj = obj?.[part];
    if (!obj) return [];
  }

  function buildTree(items) {
    if (!items || !Array.isArray(items)) return [];
    return items.map(item => ({
      name: item.nodeName || item.name,
      url: item.href ? (item.href.startsWith('http') ? item.href : 'https://www2.hm.com' + item.href) : null,
      children: buildTree(item.children)
    }));
  }

  return buildTree(obj);
}
