function parseNavigationAPI(response) {
  const data = typeof response === 'string' ? JSON.parse(response) : response;
  const results = [];

  if (!data.categories) return results;

  function extractCategory(category) {
    const categoryItem = {
      name: category.name || category.key,
      url: '',
      children: []
    };

    // Build URL from SEO data if available
    if (category.seo && category.seo.keyword) {
      categoryItem.url = `https://www.zara.com/us/en/${category.seo.keyword}-l${category.seo.seoCategoryId}.html`;
    }

    // Process subcategories recursively
    if (category.subcategories && category.subcategories.length > 0) {
      category.subcategories.forEach(subcat => {
        const subItem = extractCategory(subcat);
        if (subItem.name && !subItem.name.match(/^(THE NEW|PARTYWEAR|GIFTS|GIFT CARD)$/)) {
          categoryItem.children.push(subItem);
        }
      });
    }

    return categoryItem;
  }

  // Process main categories
  data.categories.forEach(category => {
    const categoryItem = extractCategory(category);
    if (categoryItem.name) {
      results.push(categoryItem);
    }
  });

  return results;
}
