import React, { useState, useEffect } from 'react';
import { FashionArchiveAPI } from '../services/api';

function ImageViewerPanel({ 
  images, 
  currentImageIndex, 
  galleryMode, 
  zoomMode,
  isDownloading,
  hasVideo,
  designerName,
  onPrevImage, 
  onNextImage, 
  onToggleGallery, 
  onCycleZoom,
  onToggleVideo,
  onImageSelect 
}) {
  const [zoomWindow, setZoomWindow] = useState(null);
  const [mousePos, setMousePos] = useState({ x: 0, y: 0 });

  // Zoom mode labels (matches tkinter)
  const getZoomButtonText = () => {
    switch (zoomMode) {
      case 0: return '✕';
      case 1: return '2×';
      case 2: return '3×';
      default: return '✕';
    }
  };

  // Handle mouse movement for zoom (matches tkinter magnification)
  const handleMouseMove = (e) => {
    if (zoomMode > 0 && images.length > 0) {
      const rect = e.currentTarget.getBoundingClientRect();
      setMousePos({
        x: e.clientX - rect.left,
        y: e.clientY - rect.top
      });
    }
  };

  // Get current image path
  const getCurrentImagePath = () => {
    if (images.length === 0 || currentImageIndex >= images.length) {
      return null;
    }
    return images[currentImageIndex];
  };

  // Extract look number from filename (matches tkinter logic)
  const getLookInfo = () => {
    const imagePath = getCurrentImagePath();
    if (!imagePath) return { lookNumber: 0, total: images.length };

    const filename = imagePath.split('/').pop() || '';
    const lookMatch = filename.match(/-(\d+)\./);
    const lookNumber = lookMatch ? parseInt(lookMatch[1]) : currentImageIndex + 1;

    return { lookNumber, total: images.length };
  };

  if (isDownloading) {
    return (
      <div className="mac-panel" style={{ height: '100%', display: 'flex', flexDirection: 'column' }}>
        <div className="mac-label title">Downloading images...</div>
        <div className="loading">
          <div className="mac-label">Please wait...</div>
        </div>
      </div>
    );
  }

  if (galleryMode) {
    // Gallery Grid View (matches tkinter 4-column layout)
    return (
      <div className="mac-panel" style={{ height: '100%', display: 'flex', flexDirection: 'column' }}>
        {/* Gallery Header */}
        <div style={{ display: 'flex', alignItems: 'center', padding: '8px' }}>
          <div className="mac-label title" style={{ flex: 1, margin: 0 }}>
            {designerName} Gallery
          </div>
          <button 
            className="mac-button" 
            onClick={onToggleGallery}
            style={{ marginLeft: '8px' }}
          >
            single
          </button>
        </div>

        {/* Gallery Grid */}
        <div className="gallery-grid mac-scrollbar" style={{ flex: 1 }}>
          {images.map((imagePath, index) => (
            <div
              key={index}
              className={`gallery-item ${index === currentImageIndex ? 'selected' : ''}`}
              onClick={() => onImageSelect(index)}
            >
              <img 
                src={FashionArchiveAPI.getImageUrl(imagePath)}
                alt={`Look ${index + 1}`}
                onError={(e) => {
                  e.target.style.display = 'none';
                }}
              />
            </div>
          ))}
        </div>
      </div>
    );
  }

  // Single Image View (matches tkinter default view)
  const { lookNumber, total } = getLookInfo();
  const currentImagePath = getCurrentImagePath();

  return (
    <div className="mac-panel" style={{ height: '100%', display: 'flex', flexDirection: 'column' }}>
      {/* Header with title and status */}
      <div style={{ display: 'flex', alignItems: 'center', padding: '8px' }}>
        <div className="mac-label title" style={{ flex: 1, margin: 0 }}>
          {total > 0 ? `Look ${lookNumber} / ${total}` : designerName}
        </div>
        <div className="mac-label" style={{ fontSize: '12px', color: '#666' }}>
          {/* Status info can go here */}
        </div>
      </div>

      {/* Top Controls (matches tkinter layout) */}
      <div style={{ display: 'flex', padding: '8px', gap: '8px' }}>
        <button 
          className="mac-button" 
          onClick={onToggleGallery}
          disabled={images.length === 0}
        >
          gallery
        </button>

        {hasVideo && (
          <button 
            className="mac-button" 
            onClick={onToggleVideo}
          >
            📹
          </button>
        )}

        <button 
          className="mac-button" 
          onClick={onCycleZoom}
          disabled={images.length === 0}
        >
          {getZoomButtonText()}
        </button>
      </div>

      {/* Main Image Display */}
      <div 
        className="image-container" 
        style={{ flex: 1, position: 'relative' }}
        onMouseMove={handleMouseMove}
        onMouseLeave={() => setZoomWindow(null)}
      >
        {currentImagePath ? (
          <img 
            src={FashionArchiveAPI.getImageUrl(currentImagePath)}
            alt={`Look ${lookNumber}`}
            style={{ 
              maxWidth: '100%', 
              maxHeight: '100%', 
              objectFit: 'contain',
              cursor: zoomMode > 0 ? 'crosshair' : 'default'
            }}
            onError={(e) => {
              e.target.alt = 'Image not found';
              e.target.style.background = '#f0f0f0';
            }}
          />
        ) : (
          <div className="mac-label">No images loaded</div>
        )}

        {/* Zoom Window (matches tkinter magnification) */}
        {zoomMode > 0 && currentImagePath && (
          <div
            style={{
              position: 'absolute',
              bottom: '10px',
              right: '10px',
              width: '150px',
              height: '150px',
              border: '2px outset var(--mac-bg)',
              backgroundColor: 'white',
              pointerEvents: 'none',
              overflow: 'hidden',
              zIndex: 10
            }}
          >
            <img 
              src={FashionArchiveAPI.getImageUrl(currentImagePath)}
              alt="Zoomed view"
              style={{
                transform: `scale(${zoomMode === 1 ? 2 : 3})`,
                transformOrigin: `${mousePos.x}px ${mousePos.y}px`,
                width: '100%',
                height: '100%',
                objectFit: 'contain'
              }}
            />
          </div>
        )}
      </div>

      {/* Bottom Navigation (matches tkinter) */}
      <div style={{ display: 'flex', alignItems: 'center', padding: '8px' }}>
        <button 
          className="mac-button" 
          onClick={onPrevImage}
          disabled={images.length <= 1}
        >
          ◀ Previous
        </button>

        <div style={{ flex: 1, textAlign: 'center' }}>
          {/* Counter space - using title instead */}
        </div>

        <button 
          className="mac-button" 
          onClick={onNextImage}
          disabled={images.length <= 1}
        >
          Next ▶
        </button>
      </div>
    </div>
  );
}

export default ImageViewerPanel;
