import React, { useState, useEffect } from 'react';
import { FashionArchiveAPI } from '../services/api';

function ImageViewerPanel({ 
  images, 
  currentImageIndex, 
  galleryMode, 
  zoomMode,
  isDownloading,
  hasVideo,
  designerName,
  onPrevImage, 
  onNextImage, 
  onToggleGallery, 
  onCycleZoom,
  onToggleVideo,
  onImageSelect 
}) {
  const [zoomWindow, setZoomWindow] = useState(null);
  const [mousePos, setMousePos] = useState({ x: 0, y: 0 });
  const [showZoom, setShowZoom] = useState(false);

  // Zoom mode labels (matches tkinter)
  const getZoomButtonText = () => {
    switch (zoomMode) {
      case 0: return 'âœ•';
      case 1: return '2Ã—';
      case 2: return '3Ã—';
      default: return 'âœ•';
    }
  };

  // Handle mouse movement for zoom (matches tkinter magnification)
  const handleMouseMove = (e) => {
    if (zoomMode > 0 && images.length > 0) {
      const container = e.currentTarget;
      const containerRect = container.getBoundingClientRect();
      const img = container.querySelector('img');

      if (img) {
        const imgRect = img.getBoundingClientRect();

        // Check if mouse is over the image
        const mouseX = e.clientX;
        const mouseY = e.clientY;

        if (mouseX >= imgRect.left && mouseX <= imgRect.right && 
            mouseY >= imgRect.top && mouseY <= imgRect.bottom) {

          // Calculate relative position within the image
          const relativeX = ((mouseX - imgRect.left) / imgRect.width) * 100;
          const relativeY = ((mouseY - imgRect.top) / imgRect.height) * 100;

          setMousePos({ x: relativeX, y: relativeY });
          setShowZoom(true);
        } else {
          setShowZoom(false);
        }
      }
    }
  };

  // Handle mouse leave
  const handleMouseLeave = () => {
    setShowZoom(false);
  };

  // Get current image path
  const getCurrentImagePath = () => {
    if (images.length === 0 || currentImageIndex >= images.length) {
      return null;
    }
    return images[currentImageIndex];
  };

  // Extract look number from filename (matches tkinter logic)
  const getLookInfo = () => {
    const imagePath = getCurrentImagePath();
    if (!imagePath) return { lookNumber: 0, total: images.length };

    const filename = imagePath.split('/').pop() || '';
    const lookMatch = filename.match(/-(\d+)\./);
    const lookNumber = lookMatch ? parseInt(lookMatch[1]) : currentImageIndex + 1;

    return { lookNumber, total: images.length };
  };

  if (isDownloading) {
    return (
      <div className="mac-panel" style={{ height: '100%', display: 'flex', flexDirection: 'column' }}>
        <div className="mac-label title">Downloading images...</div>
        <div className="loading">
          <div className="mac-label">Please wait...</div>
        </div>
      </div>
    );
  }

  if (galleryMode) {
    // Gallery Grid View (matches tkinter 4-column layout)
    return (
      <div className="mac-panel" style={{ height: '100%', display: 'flex', flexDirection: 'column' }}>
        {/* Gallery Header */}
        <div style={{ display: 'flex', alignItems: 'center', padding: '8px' }}>
          <div className="mac-label title" style={{ flex: 1, margin: 0 }}>
            {designerName} Gallery
          </div>
          <button 
            className="mac-button" 
            onClick={onToggleGallery}
            style={{ marginLeft: '8px' }}
          >
            single
          </button>
        </div>

        {/* Gallery Grid */}
        <div className="gallery-grid mac-scrollbar" style={{ flex: 1 }}>
          {images.map((imagePath, index) => (
            <div
              key={index}
              className={`gallery-item ${index === currentImageIndex ? 'selected' : ''}`}
              onClick={() => onImageSelect(index)}
            >
              <img 
                src={FashionArchiveAPI.getImageUrl(imagePath)}
                alt={`Look ${index + 1}`}
                onError={(e) => {
                  e.target.style.display = 'none';
                }}
              />
            </div>
          ))}
        </div>
      </div>
    );
  }

  // Single Image View (matches tkinter default view)
  const { lookNumber, total } = getLookInfo();
  const currentImagePath = getCurrentImagePath();

  return (
    <div className="mac-panel" style={{ height: '100%', display: 'flex', flexDirection: 'column' }}>
      {/* Header with title and status */}
      <div style={{ display: 'flex', alignItems: 'center', padding: '8px' }}>
        <div className="mac-label title" style={{ flex: 1, margin: 0 }}>
          {total > 0 ? `Look ${lookNumber} / ${total}` : designerName}
        </div>
        <div className="mac-label" style={{ fontSize: '12px', color: '#666' }}>
          {/* Status info can go here */}
        </div>
      </div>

      {/* Top Controls (matches tkinter layout) */}
      <div style={{ display: 'flex', padding: '8px', gap: '8px' }}>
        <button 
          className="mac-button" 
          onClick={onToggleGallery}
          disabled={images.length === 0}
        >
          gallery
        </button>

        {hasVideo && (
          <button 
            className="mac-button" 
            onClick={onToggleVideo}
          >
            ðŸ“¹
          </button>
        )}

        <button 
          className="mac-button" 
          onClick={onCycleZoom}
          disabled={images.length === 0}
        >
          {getZoomButtonText()}
        </button>
      </div>

      {/* Main Image Display */}
      <div 
        className="image-container" 
        style={{ 
          flex: 1, 
          position: 'relative', 
          display: 'flex', 
          alignItems: 'center', 
          justifyContent: 'center',
          minHeight: 0, // Allow flex shrinking
          overflow: 'hidden'
        }}
        onMouseMove={handleMouseMove}
        onMouseLeave={handleMouseLeave}
      >
        {currentImagePath ? (
          <img 
            src={FashionArchiveAPI.getImageUrl(currentImagePath)}
            alt={`Look ${lookNumber}`}
            style={{ 
              maxWidth: '100%', 
              maxHeight: '100%', 
              width: 'auto',
              height: 'auto',
              objectFit: 'contain',
              cursor: zoomMode > 0 ? 'crosshair' : 'default',
              display: 'block'
            }}
            onError={(e) => {
              e.target.alt = 'Image not found';
              e.target.style.background = '#f0f0f0';
            }}
          />
        ) : (
          <div className="mac-label">No images loaded</div>
        )}

        {/* Zoom Window (matches tkinter magnification) */}
        {zoomMode > 0 && currentImagePath && showZoom && (
          <div
            style={{
              position: 'absolute',
              bottom: '10px',
              right: '10px',
              width: '150px',
              height: '150px',
              border: '2px outset var(--mac-bg)',
              backgroundColor: 'white',
              pointerEvents: 'none',
              overflow: 'hidden',
              zIndex: 10
            }}
          >
            <img 
              src={FashionArchiveAPI.getImageUrl(currentImagePath)}
              alt="Zoomed view"
              style={{
                width: `${zoomMode === 1 ? 300 : 450}px`, // 2x or 3x size
                height: `${zoomMode === 1 ? 300 : 450}px`,
                objectFit: 'contain',
                position: 'absolute',
                left: `${75 - mousePos.x * (zoomMode === 1 ? 3 : 4.5)}px`,
                top: `${75 - mousePos.y * (zoomMode === 1 ? 3 : 4.5)}px`
              }}
            />
          </div>
        )}
      </div>

      {/* Bottom Navigation (matches tkinter) - Always visible */}
      <div style={{ 
        display: 'flex', 
        alignItems: 'center', 
        padding: '8px',
        borderTop: '1px solid var(--mac-border)',
        backgroundColor: 'var(--mac-bg)',
        flexShrink: 0 // Prevent shrinking
      }}>
        <button 
          className="mac-button" 
          onClick={onPrevImage}
          disabled={images.length <= 1}
          style={{ minWidth: '80px' }}
        >
          â—€ Previous
        </button>

        <div style={{ 
          flex: 1, 
          textAlign: 'center',
          fontSize: '12px',
          color: '#666'
        }}>
          {images.length > 0 ? `${currentImageIndex + 1} of ${images.length}` : ''}
        </div>

        <button 
          className="mac-button" 
          onClick={onNextImage}
          disabled={images.length <= 1}
          style={{ minWidth: '80px' }}
        >
          Next â–¶
        </button>
      </div>
    </div>
  );
}

export default ImageViewerPanel;
