import React, { useEffect } from 'react';
import { FashionArchiveAPI } from '../services/api';

function VideoModal({ videoPath, onClose }) {
  // Handle escape key to close (matches tkinter behavior)
  useEffect(() => {
    const handleKeyPress = (e) => {
      if (e.key === 'Escape') {
        onClose();
      }
    };

    window.addEventListener('keydown', handleKeyPress);
    return () => window.removeEventListener('keydown', handleKeyPress);
  }, [onClose]);

  return (
    <>
      {/* Modal backdrop */}
      <div className="modal-backdrop" onClick={onClose} />

      {/* Video modal - simple popup (matches tkinter requirement) */}
      <div className="video-modal">
        {/* Title bar */}
        <div className="mac-title-bar" style={{ display: 'flex', justifyContent: 'space-between' }}>
          <span>Fashion Show Video</span>
          <button 
            onClick={onClose}
            style={{ 
              background: 'none', 
              border: 'none', 
              color: 'black',
              cursor: 'pointer',
              padding: '0 4px'
            }}
          >
            âœ•
          </button>
        </div>

        {/* Video player - simple HTML5 video */}
        <video 
          controls 
          autoPlay
          style={{ 
            display: 'block',
            maxWidth: '800px',
            maxHeight: '600px',
            width: '100%'
          }}
        >
          <source src={FashionArchiveAPI.getVideoUrl(videoPath)} type="video/mp4" />
          Your browser does not support the video tag.
        </video>
      </div>
    </>
  );
}

export default VideoModal;
