import React, { useState } from 'react';
import '../styles/global.css';

function LoginModal({ isOpen, onLogin, onClose }) {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState('');
  const [showRegisterOption, setShowRegisterOption] = useState(false);
  const [isRegistering, setIsRegistering] = useState(false);

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!username.trim() || !password.trim()) {
      setError('Please enter both username and password');
      return;
    }

    setIsLoading(true);
    setError('');

    try {
      const response = await fetch('http://localhost:8081/api/auth/login', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          username: username.trim(),
          password: password,
          action: isRegistering ? 'register' : 'login'
        }),
      });

      const data = await response.json();

      if (data.success) {
        // Store session token in localStorage
        localStorage.setItem('fashionArchiveToken', data.session_token);
        localStorage.setItem('fashionArchiveUser', JSON.stringify(data.user));

        // Call parent component's login handler
        onLogin(data.user, data.session_token);

        // Clear form
        setUsername('');
        setPassword('');
        setShowRegisterOption(false);
        setIsRegistering(false);

      } else {
        // Check if we should show registration option
        if (data.can_register) {
          setShowRegisterOption(true);
          setError(`User '${username}' not found.`);
        } else {
          setError(data.error || 'Login failed');
        }
      }
    } catch (error) {
      setError('Connection failed. Please check if the server is running.');
      console.error('Login error:', error);
    } finally {
      setIsLoading(false);
    }
  };

  const handleCreateAccount = () => {
    setIsRegistering(true);
    setShowRegisterOption(false);
    setError('');
  };

  const handleBackToLogin = () => {
    setIsRegistering(false);
    setShowRegisterOption(false);
    setError('');
  };

  if (!isOpen) return null;

  return (
    <div className="modal-overlay">
      <div className="modal-content login-modal">
        <div className="window-header">
          <div className="window-title">
            {isRegistering ? 'ðŸŽ­ Create Account - Fashion Archive' : 'ðŸŽ­ Welcome to Fashion Archive'}
          </div>
        </div>

        <div className="modal-body">
          <div className="login-form-container">
            <div className="login-intro">
              {isRegistering ? (
                <p>Create your personal fashion archive account:</p>
              ) : (
                <p>Enter your name and password to access your personal fashion archive:</p>
              )}
            </div>

            <form onSubmit={handleSubmit} className="login-form">
              <div className="form-group">
                <label htmlFor="username">Name:</label>
                <input
                  type="text"
                  id="username"
                  value={username}
                  onChange={(e) => setUsername(e.target.value)}
                  placeholder="Enter your name"
                  maxLength={35}
                  disabled={isLoading}
                  autoFocus
                />
              </div>

              <div className="form-group">
                <label htmlFor="password">Password:</label>
                <input
                  type="password"
                  id="password"
                  value={password}
                  onChange={(e) => setPassword(e.target.value)}
                  placeholder="Enter your password"
                  disabled={isLoading}
                />
              </div>

              {error && (
                <div className="error-message">
                  {error}
                </div>
              )}

              <div className="form-actions">
                <button 
                  type="submit" 
                  disabled={isLoading || !username.trim() || !password.trim()}
                  className="btn btn-primary"
                >
                  {isLoading ? 'Please wait...' : (isRegistering ? 'Create Account' : 'Login')}
                </button>

                {showRegisterOption && !isRegistering && (
                  <button 
                    type="button"
                    onClick={handleCreateAccount}
                    className="btn btn-secondary"
                    disabled={isLoading}
                  >
                    Create New Account
                  </button>
                )}

                {isRegistering && (
                  <button 
                    type="button"
                    onClick={handleBackToLogin}
                    className="btn btn-secondary"
                    disabled={isLoading}
                  >
                    Back to Login
                  </button>
                )}
              </div>
            </form>

            <div className="login-footer">
              <small>
                {isRegistering ? (
                  "Your account will be created with a personal data space for favorites, brands, and downloads."
                ) : (
                  "New here? Enter any name and password, then click 'Create New Account' if prompted."
                )}
              </small>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default LoginModal;
